function generateLangMap () {
    const langNames = new Intl.DisplayNames(['en'], {type: 'region'})
    const langMap = {}
    for (let i = 0; i < 26; i++) {
      for (let j = 0; j < 26; j++) {
        let code = String.fromCharCode(97 + i) + String.fromCharCode(97 + j)
        let name = langNames.of(code)!
        if (name !== code) {
          (langMap as any)[name] = code
        }
      }
    }
    const langMap2 = {
      // Avoid using deprecated codes:
      'Akan': 'ak',
      'Hebrew': 'he',
      'Indonesian': 'id',
      'Javanese': 'jv',
      'Romanian': 'ro',
      'Yiddish': 'yi',
      // Optional extras:
      'Tagalog': 'tl',
    }
    return { ...langMap, ...langMap2 }
}

const langMap = generateLangMap()


export type IconType = 'browser' | 'country' | 'provider' | 'network'

export function getIcon(type: IconType, query: string) {
  switch(type) {
    case 'browser': return browserIcons.get(query);
    case 'country': return `https://www.countryflagicons.com/FLAT/64/${getCountryCodeFromName(query)}.png`;
    case 'network': return networkIcons.get(query);
    case 'provider': return walletProviderIcons.get(query)
  }
}

