function reverseCodes() {
  var reversed = new Map<string, string>()
  
  for(let key in isoCountries) {
    let value = isoCountries[key]
    reversed.set(value!, key)
  }
  return reversed
}

const reversed = reverseCodes()

export function getCountryCodeFromName(countryName: string): string {
  
  return reversed.get(countryName)!

}
